import React from 'react'
import {
    Chart as ChartJs,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
} from 'chart.js'

import { Line } from 'react-chartjs-2'
import styled from 'styled-components'
import { UseGlobalContext } from '../../context/globalContext';
import { DateFormat } from '../../utils/dateFormat';

ChartJs.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
)

function Chart() {
    const { incomes, expenses } = UseGlobalContext()

    /* Sort dates in asc order */
    const sortedDateIncomes = [...incomes].sort((a, b) => new Date(a.date) - new Date(b.date));
    

    const data = {
        labels: incomes.map((inc) => {
            const { date } = inc
            return DateFormat(date)
        }),

        datasets: [
            {
                label: 'Income',
                data: sortedDateIncomes.map((income) => {
                    const { amount } = income
                    return amount
                }),
                backgroundColor: '#222260',
                tension: .5
            },
            {
                label: 'Expenses',
                data: expenses.map((expense) => {
                    const { amount } = expense
                    return amount
                }),
                backgroundColor: '#A32CC4',
                tension: .5
            }
        ]

    }

    return (
        <ChartStyled>
            <Line data={data} />
        </ChartStyled>
    )
}

const ChartStyled = styled.div`
    background: #FCF6F9;
    border: 2px solid #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    padding: 1rem;
    border-radius: 20px;
    height: 100%;
`;

export default Chart
