import React from 'react'
import styled from 'styled-components'

function Button({ name, icon, onClick, bg, bPad, color, bRad }) {
    return (
        <ButtonStyled style={{
            background: bg,
            padding: bPad,
            borderRadius: bRad,
            color: color,
        }} onClick = {onClick}>
            {icon}&nbsp;{name}
        </ButtonStyled>
    )
}

const ButtonStyled = styled.button`
    outline: none;
    border: none;
    font-family: inherit;
    font-size: inherit;
    
    align-items: center;
    gap: .5rem;
    transition: all .4s ease-in-out;
`;

export default Button
